'use strict';

class Evented {
    constructor(element) {
        if (element.appendChild && element.addEventListener) {
            this.element = element;
        } else {
            return null
        }
    }

    on(ev, callback) {
        this.element.addEventListener(ev, (e) => {
            if (callback) {
                callback({
                    ev: e,
                    el: this
                });
            }
        });
    }

    fire(ev, obj) {
        if (ev instanceof Event) {
            this.element.dispatchEvent(ev);
            console.log("WARNING: event provided we do not check if it is a valid event");
        } else { //assume e is string
            this.element.dispatchEvent(new CustomEvent(ev, {
                detail: obj
            }));
        }
    }
}

module.exports = Evented;
